import React, { useState, useEffect } from "react";
import { useHistory, Link } from "react-router-dom";
import axios from "axios";
import { makeStyles } from "@material-ui/core/styles";
import InputLabel from "@material-ui/core/InputLabel";
import MenuItem from "@material-ui/core/MenuItem";
import FormControl from "@material-ui/core/FormControl";
import Select from "@material-ui/core/Select";

function Favourites() {
  const history = useHistory();
  const [favouriteItems, setFavouriteItems] = useState([]);
  const [locations, setLocations] = useState([]);

  const useStyles = makeStyles((theme) => ({
    formControl: {
      margin: theme.spacing(1),
      minWidth: 520,
    },
    selectEmpty: {
      marginTop: theme.spacing(2),
    },
  }));

  const classes = useStyles();

  const loadFavouries = (event) => {
    const API_URL = "http://127.0.0.1:8000/test/listfavourites/";
    axios
      .get(API_URL)
      .then((response) => {
        console.log(response);
        setFavouriteItems(response.data.results);
      })
      .catch((error) => {
        console.log(error);
      });
  };

  const addNote = (id) => {
    const API_URL = "http://127.0.0.1:8000/test/addfavourite/";
    axios
      .post(API_URL, {
        id: id,
      })
      .then(function (response) {
        console.log(response);
      })
      .catch(function (error) {
        console.log(error);
      });
  };
  useEffect(() => {
    loadFavouries();
  }, []);
  return (
    <React.Fragment>
      <div className="container center ">
        <h2>Favourites</h2>
      </div>
      <div className="row">
        {favouriteItems.map((image, index) => {
          console.log(image);
          let srcPath =
            "https://farm" +
            image.farm +
            ".staticflickr.com/" +
            image.server +
            "/" +
            image.id +
            "_" +
            image.secret +
            ".jpg";
          return (
            <div className="container center">
              <div className=" center">
                <div className="col s6 m4">
                  <div className="card">
                    <div className="card-image">

                      <img height="300" key={index} src={srcPath}></img>
                      <label className="btn-floating  waves-effect waves-light ">
                          <a
                            class="material-icons"
                            onClick={() => {
                              addNote();
                            }}
                          >
                          Add Note
                          </a>
                        </label>
                    </div>
                    {/* <div className="card-action center">
                      <p>
                        <label className="btn-floating btn-large waves-effect waves-light red">
                          <i
                            class="material-icons"
                            onClick={() => {
                              addNote();
                            }}
                          >
                            add
                          </i>
                          Add Note
                        </label>
                      </p>
                    </div> */}
                  </div>
                </div>
              </div>
            </div>
          );
        })}
      </div>
    </React.Fragment>
  );
}

export default Favourites;

