import React, { useState, useEffect } from "react";
import axios from "axios";
import Display from "./Display";

const OPTIONS = []
let pageIndex = 0;
let max_page = 1000;

function Input(props) {
  const API_URL = "http://127.0.0.1:8000/test/search/";
  const [options, setOptions] = useState([]);
  const [images, setImages] = useState([]);
  const [view, setView] = useState(false);
  const [display, setDisplay] = useState([]);
  const [values, setValues] = useState({
    latitude: "",
    longitude: "",
  });

  const set = (name) => {
    return ({ target: { value } }) => {
      setValues((oldValues) => ({ ...oldValues, [name]: value }));
    };
  };
  const Search = () => {
    const url = "http://127.0.0.1:8000/test/search/";
    axios
      .get(API_URL, {
        params: values,
      })
      .then(function (response) {
        console.log(response);
        setImages(response.data.photos.photo);
        console.log(images);
      })
      .catch(function (error) {
        console.log(error);
      });
  };
  const onSubmit = async (event) => {
    event.preventDefault(); // Prevent default submission
    try {
      await Search();
      setView(true);
    } catch (e) {
      alert(`Search failed! ${e.message}`);
    }
  };
  const populate = () => {
    axios
      .get("http://localhost:8000/test/populate/")
      .then((response) => {
        console.log(response.data);
        setOptions(response.data);
      })
      .catch((error) => {
        console.log(error);
      });
  };
  console.log(options)
  useEffect(() => {
    // Update the document title using the browser API
    populate();
  }, []);

  if (view){
    setDisplay(images.photo.slice(0, 10));
    const nextPage = () => {
      pageIndex += 1;
      updateDisplay();
      console.log(pageIndex);
    };
    const prevPage = () => {
      pageIndex -= 1;
      updateDisplay();
      console.log(pageIndex);
    };

    const updateDisplay = () => {
      setDisplay(props.images.slice(10 * pageIndex, (pageIndex + 1) * 10));
    };

    const addToFavourite = () => {
      const url = "http://127.0.0.1:8000/test/addfavourite/";
      axios
        .post(url, {
          location: "",
          image_src: "Flintstone",
        })
        .then(function (response) {
          console.log(response);
        })
        .catch(function (error) {
          console.log(error);
        });
    };
    console.log(display, "display");
    // const srcPath = 'https://farm'+pic.farm+'.staticflickr.com/'+pic.server+'/'+pic.id+'_'+pic.secret+'.jpg';

    return( 
    <div className="container">
      {display.map((image) => {
        let srcPath =
          "https://farm" +
          image.farm +
          ".staticflickr.com/" +
          image.server +
          "/" +
          image.id +
          "_" +
          image.secret +
          ".jpg";
        console.log(srcPath);
        return (
          <div className="row center">
            <div className="col s6 m4">
              <div className="card">
                <div className="card-image">
                  <img src={srcPath}></img>
                </div>
                <div class="card-action center">
                  <a onClick={addToFavourite()}>Add to favourites</a>
                </div>
              </div>
            </div>
          </div>
        );
      })}
      <div>
        <div className="container center">
          {pageIndex != 0 ? (
            <button className="btn waves-effect waves-light" onClick={prevPage}>
              Prev
            </button>
          ) : (
            <></>
          )}
          <h3>---{pageIndex}---</h3>

          {pageIndex < max_page ? (
            <button className="btn waves-effect waves-light" onClick={nextPage}>
              Next
            </button>
          ) : (
            <></>
          )}

          {/* <button onClick = {prevPage}>Prev</button>
         <button className = "btn waves-effect waves-light" onClick = {nextPage}>next</button> */}
        </div>
      </div>
    </div>
    );
}

  return (
    <div className="container">
      <div>
        <h2 className="center">Picture Search By Geolocation</h2>
      </div>
      <form onSubmit={onSubmit} className="col s12">
        <div className="row">
          <div className="input-field col s6">
            <input
              placeholder="Latitude"
              id="latitude"
              value={values.latitude}
              onChange={set("latitude")}
            />
          </div>
          <div className="input-field col s6">
            <input
              placeholder="Longitude"
              id="longitude"
              value={values.longitude}
              onChange={set("longitude")}
            />
          </div>
        </div>
        <div className="center">
          <button className="btn waves-effect waves-light" name="action">
            Submit
            <i className="material-icons right">send</i>
          </button>
        </div>
      </form>
      <div className="center">
        <h2>or</h2>
      </div>
      <div className=" col s12">
        <select>
          <option value="">Location</option>
          <option>vdsvafa</option>
          {options.map((option) => (
            <option value={option.location_name} key={option.location_name}>
              {option.location_name}
            </option>
          ))}
        </select>
      </div>
    </div>
  );
}

export default Input;
