import React, { useState, useEffect } from "react";
import axios from "axios";
import { Link } from "react-router-dom";
import Image from "./Image";

function Input(props) {
  const [options, setOptions] = useState([]);
  const config = {
    headers: { "Access-Control-Allow-Origin": "https://localhost:8000/" },
  };
  // axios
  //   .get("http://localhost:8000/test/populate/", config)
  //   .then((response) => {
  //     console.log(response);
  //   })
  //   .catch((error) => {
  //     console.log(error);
  //   });

  const showImages = async (event) => {
    event.preventDefault();

    const response = await axios.get(
      "https://www.flickr.com/services/rest/?method=flickr.photos.search&api_key=f5e3dc5e6886a1e7799eeb85aad99cb5&tags=chennai&per_page=10&page=2&format=json&nojsoncallback=1"
    );
    props.onSearch(response.data.photos);
  };
  return (
    <div className="container">
      <div>
        {options.map((option) => (
          <h2>{option.location_name}</h2>
        ))}
      </div>
      <div>
        <h2 className="center">Picture Search By Geolocation</h2>
      </div>
      <form className="col s12" onSubmit={showImages}>
        <div className="row">
          <div className="input-field col s6">
            <input id="latitude" type="text" className="validate" />
            <label for="latitude">Latitude</label>
          </div>
          <div className="input-field col s6">
            <input id="longitude" type="text" className="validate" />
            <label for="longitude">Longitude</label>
          </div>
        </div>
        <div className="center">
        <Link to="/result">
          <button
            className="btn waves-effect waves-light"
            type="submit"
            name="action"
          >
            Submit
            <i className="material-icons right">send</i>
          </button>
          </Link>
        </div>
      </form>
      <div className="center">
        <h2>or</h2>
      </div>
      <form>
        <div className="input-field col s12">
          <select>
            <option value="" disabled selected>
              Location
            </option>
            {options.map((option) => (
              <option>{option.location_name}</option>
            ))}
          </select>
        </div>
        <div className="center">
          <button
            className="btn waves-effect waves-light"
            type="submit"
            name="action"
          >
            Submit
            <i className="material-icons right">send</i>
          </button>
        </div>
      </form>
    </div>
  );
}

export default Input;
