import React, { useEffect, useState } from "react";
import axios from "axios";
let pageIndex = 0;
let max_page = 1000;
function Display() {
  const API_URL = "http://127.0.0.1:8000/test/search/";

  const [images, setImages] = useState([]);
  const [display, setDisplay] = useState([]);

  const showImages = () => {
    axios
      .get(API_URL, {
        params: values,
      })
      .then((response) => {
        console.log(response.data.photos.photo);
        setImages(response.data.photos.photo);
        max_page = Math.ceil(response.data.photos.photo.length / 10) - 1;
        console.log(max_page);
        setDisplay(response.data.photos.photo.slice(0, 10));
      });
  };

  const onSubmit = async (event) => {
    event.preventDefault(); // Prevent default submission
    try {
      await showImages();
    } catch (e) {
      alert(`Registration failed! ${e.message}`);
    }
  };

  const nextPage = () => {
    pageIndex += 1;
    updateDisplay();
    console.log(pageIndex);
  };
  const prevPage = () => {
    pageIndex -= 1;
    updateDisplay();
    console.log(pageIndex);
  };

  const updateDisplay = () => {
    setDisplay(images.slice(10 * pageIndex, (pageIndex + 1) * 10));
  };

  useEffect(() => {
    // Update the document title using the browser API
    showImages();
  }, []);
  console.log(display, "display");
  // const srcPath = 'https://farm'+pic.farm+'.staticflickr.com/'+pic.server+'/'+pic.id+'_'+pic.secret+'.jpg';

  return (
    <div className="container">
      {display.map((image) => {
        let srcPath =
          "https://farm" +
          image.farm +
          ".staticflickr.com/" +
          image.server +
          "/" +
          image.id +
          "_" +
          image.secret +
          ".jpg";
        console.log(srcPath);
        return (
          <div className="row center">
            <div className="col s6 m4">
              <div className="card">
                <div className="card-image">
                  <img src={srcPath}></img>
                </div>
                <div class="card-action center">
                  <a onClick={addToFavourite()}>Add to favourites</a>
                </div>
              </div>
            </div>
          </div>
        );
      })}
      <div>
        <div className="container center">
          {pageIndex != 0 ? (
            <button className="btn waves-effect waves-light" onClick={prevPage}>
              Prev
            </button>
          ) : (
            <></>
          )}
          <h3>---{pageIndex}---</h3>

          {pageIndex < max_page ? (
            <button className="btn waves-effect waves-light" onClick={nextPage}>
              Next
            </button>
          ) : (
            <></>
          )}

          {/* <button onClick = {prevPage}>Prev</button>
         <button className = "btn waves-effect waves-light" onClick = {nextPage}>next</button> */}
        </div>
      </div>
    </div>
  );
}

export default Display;
