import React, { useEffect, useState } from "react";

import axios from "axios";
import { makeStyles } from "@material-ui/core/styles";
import Grid from "@material-ui/core/Grid";
import Card from "@material-ui/core/Card";
import CardActionArea from "@material-ui/core/CardActionArea";
import CardActions from "@material-ui/core/CardActions";
import CardContent from "@material-ui/core/CardContent";
import CardMedia from "@material-ui/core/CardMedia";
import Button from "@material-ui/core/Button";
import Typography from "@material-ui/core/Typography";
import { Modal } from "@material-ui/core";

import ViewNotes from "./ViewNotes";

function Display(props) {
  const [favourites, setFavourites] = useState([]);
  let favourites_id = [];
  const [favouritesChanged, setFavouritesChanged] = useState(0);
  const [newnoteadded, setNewNoteAdded] = useState(0);
  const [open, setOpen] = useState(false);
  const [modalStyle] = useState(getModalStyle);


  console.log(props.results);

  const newNoteAdded = () => {
    setNewNoteAdded(newnoteadded+1);
  }

  function rand() {
    return Math.round(Math.random() * 20) - 10;
  }

  function getModalStyle() {
    const top = 50 + rand();
    const left = 50 + rand();

    return {
      top: `${top}%`,
      left: `${left}%`,
      transform: `translate(-${top}%, -${left}%)`,
    };
  }

  const useStyles = makeStyles((theme) => ({
    root: {
      display: "inline-grid",
    },
    media: {
      height: 200,
    },
    title: {
      height: 80,
    },
    topPadding: {
      padding: "10px",
    },
    paper: {
      position: "absolute",
      width: 400,
      backgroundColor: theme.palette.background.paper,
      border: "2px solid #000",
      boxShadow: theme.shadows[5],
      padding: theme.spacing(2, 4, 3),
    },
  }));

  const classes = useStyles();

  const listfavourites = () => {
    const API_URL = "http://127.0.0.1:8000/test/listfavourites/";
    axios
      .get(API_URL)
      .then((response) => {
        console.log(response);
        setFavourites(response.data.results);
      })
      .catch((error) => {
        console.log(error);
      });
  };

  const addToFavourite = (id, secret, farm, server, title) => {
    const API_URL = "http://127.0.0.1:8000/test/addfavourite/";
    axios
      .post(API_URL, {
        id: id,
        secret: secret,
        farm: farm,
        server: server,
        title: String(title),
      })
      .then(function (response) {
        console.log(response);
        setFavouritesChanged(favouritesChanged + 1);
      })
      .catch(function (error) {
        console.log(error);
      });
  };

  const populatefavourites = () => {
    favourites_id = [];
    favourites.map((favourite) => {
      console.log(favourite.id);
      favourites_id = [...favourites_id, favourite.id];
      console.log(favourites_id);
    });
  };

  const isPresent = (id, secret, farm, server, title) => {
    console.log(favourites);
    console.log(id);
    populatefavourites();
    console.log(favourites_id);
    {
      if (favourites_id.indexOf(id) !== -1) {
        return (
          <>
            <Modal
              open={open}
              onClose={handleClose}
              aria-labelledby="simple-modal-title"
              aria-describedby="simple-modal-description"
            >
              <>
                <div style={modalStyle} className={classes.paper}>
                  {console.log(id)}
                  <ViewNotes id={id} newnote = {newNoteAdded}  />
                </div>
              </>
            </Modal>
            <div>
              <button type="button" onClick={handleOpen}>
                View Notes
              </button>
            </div>
            {/* <a className="" href={"/addnotes/" + id}>
                <span
                  className="blue-text text-darken-2"
                  style={{ fontSize: "15px" }}
                >
                  Add Note
                </span>
              </a> */}
          </>
        );
      } else {
        return (
          <>
            <label>
              <input
                onClick={() => {
                  addToFavourite(id, secret, farm, server, title);
                }}
                type="checkbox"
              />
              <span>Add to favourites</span>
            </label>
          </>
        );
      }
    }
  };

  const handleOpen = () => {
    setOpen(true);
  };

  const handleClose = () => {
    setOpen(false);
  };

  useEffect(() => {
    listfavourites();
  }, [favouritesChanged, newnoteadded]);

  useEffect(() => {

  })

  // useEffect(() => {
  //   populatefavourites();
  // },[favourites]);
  // const srcPath = 'https://farm'+pic.farm+'.staticflickr.com/'+pic.server+'/'+pic.id+'_'+pic.secret+'.jpg';
  return (
    <div className={classes.root}>
      <Grid container spacing={3}>
        {props.results.map((image) => {
          let srcPath =
            "https://farm" +
            image.farm +
            ".staticflickr.com/" +
            image.server +
            "/" +
            image.id +
            "_" +
            image.secret +
            ".jpg";
          return (
            <Grid item xs={4}>
              <Card className={classes.image}>
                <CardActionArea>
                  <CardMedia
                    className={classes.media}
                    image={srcPath}
                    title={image.title}
                  />
                  <CardContent>
                    <Typography
                      className={classes.title}
                      gutterBottom
                      variant="h5"
                      component="h2"
                    >
                      {image.title}
                    </Typography>
                  </CardContent>
                </CardActionArea>
                <CardActions>
                  <Button
                    size="small"
                    color="primary"
                    className={classes.topPadding}
                  >
                    {isPresent(
                      image.id,
                      image.secret,
                      image.farm,
                      image.server,
                      image.title
                    )}
                  </Button>
                </CardActions>
              </Card>
            </Grid>
          );
        })}
      </Grid>
    </div>
  );
}

export default Display;
