import React, { useEffect, useState } from "react";
import axios from "axios";

function Display(props) {
  console.log(props.results);
  const images = props.results;
  const [favourites, setFavourites] = useState([]);
  const [favouritesChanged, setFavouritesChanged] = useState(false);
  console.log(images);

  const listfavourites = () => {
    const API_URL = "http://127.0.0.1:8000/test/listfavourites/";
    axios
      .get(API_URL)
      .then((response) => {
        console.log(response);
        setFavourites(response.data.results);
      })
      .catch((error) => {
        console.log(error);
      });
  };

  const addNote = (id) => {
    const API_URL = "http://127.0.0.1:8000/test/addfavourite/";
    axios
      .post(API_URL, {
        id: id,
      })
      .then(function (response) {
        console.log(response);
      })
      .catch(function (error) {
        console.log(error);
      });
  };

  const addToFavourite = (farm, server, id, secret, title) => {
    const API_URL = "http://127.0.0.1:8000/test/addfavourite/";
    axios
      .post(API_URL, {
        id: id,
        farm: farm,
        server: server,
        secret: secret,
        title: title,
      })
      .then(function (response) {
        console.log(response);
        setFavouritesChanged(true);
      })
      .catch(function (error) {
        console.log(error);
      });
  };

  const isPresent = (id) =>{
    console.log(favourites)
    console.log(id)
    if(favourites){
    return(<><p>
    <label>
      <input
        onClick={() => {
          addNote(id);
        }}
        type="checkbox"
      />
      <span>Add to favourites</span>
    </label>
  </p></>
    )
      }
  return(
  <>
  <p>
    <label>
      <input
        onClick={() => {
          addToFavourite(
            image.farm,
            image.server,
            image.id,
            image.secret,
            image.title
          );
        }}
        type="checkbox"
      />
      <span>Add to favourites</span>
    </label>
  </p>
  </>
  );
  }

  useEffect(() => {
    listfavourites();
    console.log(favourites);
  }, [favouritesChanged]);

  useEffect(() => {

  })

  // const srcPath = 'https://farm'+pic.farm+'.staticflickr.com/'+pic.server+'/'+pic.id+'_'+pic.secret+'.jpg';
  return (
    <div className="row">
      {images.map((image) => {
        let srcPath =
          "https://farm" +
          image.farm +
          ".staticflickr.com/" +
          image.server +
          "/" +
          image.id +
          "_" +
          image.secret +
          ".jpg";
        return (
          <div className="center">
            <div className="col s6 m4">
              <div className="card">
                <div className="card-image">
                  <img height="300" src={srcPath}></img>
                </div>
                <div className="card-action center">
                  {isPresent(image.id)}
                </div>
              </div>
            </div>
          </div>
        );
      })}
    </div>
  );
}

export default Display;
