import React, { useState, useEffect } from "react";
import axios from "axios";
import { Link } from "react-router-dom";
import Display from "./Display";

function Input(props) {
  const [options, setOptions] = useState([]);
  axios
    .get("http://localhost:8000/test/populate/")
    .then((response) => {
      console.log(response);
      setOptions(response)
    })
    .catch((error) => {
      console.log(error);
    });
  return (
    <div className="container">
      <div>
        {options.map((option) => (
          <h2>{option.location_name}</h2>
        ))}
      </div>
      <div>
        <h2 className="center">Picture Search By Geolocation</h2>
      </div>
      <form className="col s12">
        <div className="row">
          <div className="input-field col s6">
            <input id="latitude" type="text" className="validate" />
            <label for="latitude">Latitude</label>
          </div>
          <div className="input-field col s6">
            <input id="longitude" type="text" className="validate" />
            <label for="longitude">Longitude</label>
          </div>
        </div>
        <div className="center">
          <Link to = '/result'>
          <button
            className="btn waves-effect waves-light"
            name="action"
          >
            Submit
            <i className="material-icons right">send</i>
          </button>
          </Link>
        </div>
      </form>
      <div className="center">
        <h2>or</h2>
      </div>
        <div className=" col s12">
          <select onChange = "">
            <option value="">
              Location
            </option>
            {options.map((option) => (
              <option value = {option.location_name}>{option.location_name}</option>
            ))}
          </select>
        </div>
    </div>
  );
}

export default Input;
