import React, { useEffect, useState } from "react";
import axios from "axios";

function Display(props) {
  console.log(props.results);
  const images = props.results;
  const [favourites, setFavourites] = useState([]);
  const [favouritesChanged, setFavouritesChanged] = useState(0);
  let favourites_id = [];

  console.log(images);

  const listfavourites = (id, secret, farm, server, title) => {
    const API_URL = "http://127.0.0.1:8000/test/listfavourites/";
    axios
      .get(API_URL)
      .then((response) => {
        console.log(response);
        setFavourites(response.data.results);
      })
      .catch((error) => {
        console.log(error);
      });
    populatefavourites(id, secret, farm, server, title)
  };

  const addNote = (id, text) => {
    const API_URL = "http://127.0.0.1:8000/test/addnote/";
    axios
      .post(API_URL, {
        image_id: id,
        text: text,
      })
      .then(function (response) {
        console.log(response);
      })
      .catch(function (error) {
        console.log(error);
      });
  };

  const addToFavourite = (id, secret, farm, server, title) => {
    const API_URL = "http://127.0.0.1:8000/test/addfavourite/";
    axios
      .post(API_URL, {
        id: id,
        secret: secret,
        farm: farm,
        server: server,
        title: title,
      })
      .then(function (response) {
        console.log(response);
        setFavouritesChanged(favouritesChanged+1);
      })
      .catch(function (error) {
        console.log(error);
      });
  };

  const populatefavourites = (id, secret, farm, server, title) => {
    favourites.map((favourite) => {
      console.log(favourite.id);
      favourites_id = [...favourites_id, favourite.id];
      console.log(favourites_id)
    });
    isPresent(id, secret, farm, server, title)
  };

  const isPresent = (id, secret, farm, server, title) => {
    console.log(favourites);
    console.log(id);
    console.log(favourites_id);
    {
      if(favourites_id.indexOf(id) !== -1)   {
        return (
          <>
            <p>
              <label>
                <input
                  onClick={() => {
                    let text = "sample text";
                    addNote(id, text);
                  }}
                  type="checkbox"
                />
                <span>Add Note</span>
              </label>
            </p>
          </>
        );
      } 
      else{
        return (
          <>
            <p>
              <label>
                <input
                  onClick={() => {
                    addToFavourite(id, secret, farm, server, title);
                  }}
                  type="checkbox"
                />
                <span>Add to favourites</span>
              </label>
            </p>
          </>
        );
      };
      }
    }
    useEffect(() => {
      populatefavourites();
    },[favourites]);

  useEffect(() => {
    listfavourites();
  }, [favouritesChanged]);


  // const srcPath = 'https://farm'+pic.farm+'.staticflickr.com/'+pic.server+'/'+pic.id+'_'+pic.secret+'.jpg';
  return (
    <div className="row">
      {images.map((image) => {
        let srcPath =
          "https://farm" +
          image.farm +
          ".staticflickr.com/" +
          image.server +
          "/" +
          image.id +
          "_" +
          image.secret +
          ".jpg";
        return (
          <div className="center">
            <div className="col s6 m4">
              <div className="card">
                <div className="card-image">
                  <img height="300" src={srcPath}></img>
                </div>
                <div className="card-action center">
                  {listfavourites(
                    image.id,
                    image.secret,
                    image.farm,
                    image.server,
                    image.title
                  )}
                </div>
              </div>
            </div>
          </div>
        );
      })}
    </div>
  );
}

export default Display;
