import React from "react";
import ReactDom from "react-dom";

function Modal({ open, children, onClose }) {
  if (!open) return null;
  return(
      <>
    <div>
      {children}
      <button onClick={onClose}>close</button>
    </div>
    </>
  );
}

ReactDom.createPortal(
    <Modal/>,
    document.getElementById('poratl')
)

export default Modal;
