import React, { useEffect, useState } from "react";
import axios from "axios";
let id = 0;
let source = "";
let location = "";

function Display(props) {
  console.log(props);
  console.log(props.results);
  const [locations, setLocations] = useState([]);
  const [id, setId] = useState(0);
  const images = props.results;
  console.log(images);

  const populate = () => {
    axios
      .get("http://localhost:8000/test/populate/")
      .then((response) => {
        console.log(response.data);
        setLocations(response.data.results);
      })
      .catch((error) => {
        console.log(error);
      });
  };

  const getLocationId = () => {
    locations.map((item) => {
      if (item.location_name == props.location) {
        setId(item.id);
      }
    });
  };

  useEffect(() => {
    // Update the document title using the browser API
    populate();
    getLocationId();
  }, []);

  const addToFavourite = (id, source) => {
    const url = "http://127.0.0.1:8000/test/addfavourite/";
    axios
      .post(url, {
        location: id,
        image_src: source,
      })
      .then(function (response) {
        console.log(response);
      })
      .catch(function (error) {
        console.log(error);
      });
  };

  // const srcPath = 'https://farm'+pic.farm+'.staticflickr.com/'+pic.server+'/'+pic.id+'_'+pic.secret+'.jpg';
  return (
    <div className="row">
      {images.map((image) => {
        let srcPath =
          "https://farm" +
          image.farm +
          ".staticflickr.com/" +
          image.server +
          "/" +
          image.id +
          "_" +
          image.secret +
          ".jpg";
        return (
          <div className="center">
            <div className="col s6 m4">
              <div className="card">
                <div className="card-image">
                  <img height="300" src={srcPath}></img>
                </div>
                <div className="card-action center">
                  <p>
                    <label>
                      <input
                        onClick={() => {
                          addToFavourite(1, srcPath);
                        }}
                        type="checkbox"
                      />
                      <span>Add to favourites</span>
                    </label>
                  </p>
                </div>
              </div>
            </div>
          </div>
        );
      })}
    </div>
  );
}

export default Display;
