import React, { useEffect, useState } from "react";
import axios from "axios";
let id = 0;
let source = "";
let location = "";
let pageIndex = 1;
function Display(props) {
  const [images, setImages] = useState([props.images]);
  const nextPage = () => {
    pageIndex += 1;
    updateDisplay();
    console.log(pageIndex);
  };
  const prevPage = () => {
    pageIndex -= 1;
    updateDisplay();
    console.log(pageIndex);
  };

  const updateDisplay = () => {

  };

  const addToFavourite = (id, source) => {
    const url = "http://127.0.0.1:8000/test/addfavourite/";
    axios
      .post(url, {
        location: id,
        image_src: source,
      })
      .then(function (response) {
        console.log(response);
      })
      .catch(function (error) {
        console.log(error);
      });
  };

  // const srcPath = 'https://farm'+pic.farm+'.staticflickr.com/'+pic.server+'/'+pic.id+'_'+pic.secret+'.jpg';
  return (
    <div className="row container">
      {images.map((image) => {
        let srcPath =
          "https://farm" +
          image.farm +
          ".staticflickr.com/" +
          image.server +
          "/" +
          image.id +
          "_" +
          image.secret +
          ".jpg";
        return (
          <div className="center">
            <div className="col s6 m4">
              <div className="card">
                <div className="card-image">
                  <img height="300" src={srcPath}></img>
                </div>
                <div className="card-action center">
                  <p>
                    <label>
                      <input
                        onClick={() => {
                          addToFavourite(1, srcPath);
                        }}
                        type="checkbox"
                      />
                      <span>Add to favourites</span>
                    </label>
                  </p>
                </div>
              </div>
            </div>
          </div>
        );
      })}
      <div>
        <div className="container center">
          {pageIndex != 0 ? (
            <button className="btn waves-effect waves-light" onClick={prevPage}>
              Prev
            </button>
          ) : (
            <></>
          )}

          {
            <button className="btn waves-effect waves-light" onClick={nextPage}>
              Next
            </button>
          }

          {/* <button onClick = {prevPage}>Prev</button>
         <button className = "btn waves-effect waves-light" onClick = {nextPage}>next</button> */}
        </div>
      </div>
    </div>
  );
}

export default Display;
