import React, { useState, useEffect } from "react";
import axios from "axios";
import { useHistory } from "react-router-dom";
import Display from "./Display";


function Input() {
  const history = useHistory();
  const [change, setChange] = useState(false);
  const [images, setImages] = useState([]);
  const [latitude, setLatitude] = useState("")
  const [longitude, setLongitude] = useState("")
  const [pageIndex, setPageIndex] = useState(0);


  useEffect(() => {
    // Update the document title using the browser API
    if (change) {
      const index =  pageIndex + 1
      setValues((oldValues) => ({ ...oldValues, page: index }));

      Search()
    }
    console.log(values, change);
  }, [change]);

  const nextPage = () => {
    setChange(true);
    console.log(values);
    updateDisplay();
  };
  const prevPage = () => {
    setValues((oldValues) => ({ ...oldValues, page: pageIndex - 1 }));
    console.log(values);
    updateDisplay();
  };

  const updateDisplay = () => {
    displayImages();
  };

  const set = (name) => {
    return () => {
      setLatitude((oldValues) => ({ ...oldValues, [name]: value }));
    };
  };
  const Search = () => {
    const API_URL = "http://127.0.0.1:8000/test/search/";
    console.log(values);
    axios
      .get(API_URL, {
        params: values,
      })
      .then(function (response) {
        console.log(response.data.photos.photo);
        setImages(response.data.photos.photo);
      })
      .catch(function (error) {
        console.log(error);
      });
  };
  const displayImages = () => {
    console.log(images);
    return (
      <React.Fragment>
        {images.length > 0 && (
          <>
          <Display
            results={images}
          />
          <div className="container center">
          {pageIndex != 0 ? (
            <button className="btn waves-effect waves-light" onClick={prevPage}>
              Prev
            </button>
          ) : (
            <></>
          )}

          {
            <button className="btn waves-effect waves-light" onClick={nextPage}>
              Next
            </button>
          }

          {/* <button onClick = {prevPage}>Prev</button>
         <button className = "btn waves-effect waves-light" onClick = {nextPage}>next</button> */}
        </div>
        </>
        )}

      </React.Fragment>
    );
  };
  const onSubmit = async (event) => {
    event.preventDefault(); // Prevent default submission
    try {
      await Search();
      displayImages();
    } catch (e) {
      alert(`Search failed! ${e.message}`);
    }
  };

  return (
    <div className="container">
      <div>
        <h2 className="center">Picture Search By Geolocation</h2>
        <br />
      </div>
      <form onSubmit={onSubmit} className="col s12">
        <div className="row">
          <div className="input-field col s6">
            <input
              placeholder="Latitude"
              id="latitude"
              value={latitude}
              onChange={set("latitude")}
            />
          </div>
          <div className="input-field col s6">
            <input
              placeholder="Longitude"
              id="longitude"
              value={longitude}
              onChange={handleLongitudeChange(longitude)}
            />
          </div>
        </div>
        <div className="center">
          <button className="btn waves-effect waves-light" name="action">
            Submit
            <i className="material-icons right">send</i>
          </button>
        </div>
      </form>
      <div>{displayImages()}</div>

    </div>
  );
}

export default Input;
