import React, { useEffect, useState } from "react";

import axios from "axios";
import { makeStyles } from "@material-ui/core/styles";
import Paper from "@material-ui/core/Paper";
import Grid from "@material-ui/core/Grid";
import Card from "@material-ui/core/Card";
import CardActionArea from "@material-ui/core/CardActionArea";
import CardActions from "@material-ui/core/CardActions";
import CardContent from "@material-ui/core/CardContent";
import CardMedia from "@material-ui/core/CardMedia";
import Button from "@material-ui/core/Button";
import Typography from "@material-ui/core/Typography";

const useStyles = makeStyles({
  root: {
    flexGrow: 1,
    // maxWidth: 345,
  },
  media: {
    height: 140,
  },
});

function Display(props) {
  console.log(props.results);

  const classes = useStyles();

  const [favourites, setFavourites] = useState([]);
  let favourites_id = [];

  const [favouritesChanged, setFavouritesChanged] = useState(0);

  const listfavourites = () => {
    const API_URL = "http://127.0.0.1:8000/test/listfavourites/";
    axios
      .get(API_URL)
      .then((response) => {
        console.log(response);
        setFavourites(response.data.results);
      })
      .catch((error) => {
        console.log(error);
      });
  };

  const addToFavourite = (id, secret, farm, server, title) => {
    const API_URL = "http://127.0.0.1:8000/test/addfavourite/";
    axios
      .post(API_URL, {
        id: id,
        secret: secret,
        farm: farm,
        server: server,
        title: String(title),
      })
      .then(function (response) {
        console.log(response);
        setFavouritesChanged(favouritesChanged + 1);
      })
      .catch(function (error) {
        console.log(error);
      });
  };

  const populatefavourites = () => {
    favourites_id = [];
    favourites.map((favourite) => {
      console.log(favourite.id);
      favourites_id = [...favourites_id, favourite.id];
      console.log(favourites_id);
    });
  };

  const isPresent = (id, secret, farm, server, title) => {
    console.log(favourites);
    console.log(id);
    populatefavourites();
    console.log(favourites_id);
    {
      if (favourites_id.indexOf(id) !== -1) {
        return (
          <>
            <p>
              <a className="" href={"/addnotes/" + id}>
                <span
                  className="blue-text text-darken-2"
                  style={{ fontSize: "17px" }}
                >
                  Add Note
                </span>
              </a>
            </p>
          </>
        );
      } else {
        return (
          <>
            <p>
              <label>
                <input
                  onClick={() => {
                    addToFavourite(id, secret, farm, server, title);
                  }}
                  type="checkbox"
                />
                <span>Add to favourites</span>
              </label>
            </p>
          </>
        );
      }
    }
  };

  useEffect(() => {
    listfavourites();
  }, [favouritesChanged]);

  // const srcPath = 'https://farm'+pic.farm+'.staticflickr.com/'+pic.server+'/'+pic.id+'_'+pic.secret+'.jpg';
  return (
    <div>
      {props.results.map((image) => {
        let srcPath =
          "https://farm" +
          image.farm +
          ".staticflickr.com/" +
          image.server +
          "/" +
          image.id +
          "_" +
          image.secret +
          ".jpg";
        return (
          <div className={classes.root}>
            <Grid container spacing={3}>
              <Grid item xs={6}>
              <Card className={classes.root}>
              <CardActionArea>
                <Typography variant="body2" color="textSecondary" component="p">
                  Lizards are a widespread group of squamate reptiles, with over
                  6,000 species, ranging across all continents except Antarctica
                </Typography>{" "}
                <CardMedia className={classes.media} image={srcPath} title="" />
                <CardContent>
                  <Typography gutterBottom variant="h5" component="h2">
                    {image.title}
                  </Typography>
                  {/* <Typography variant="body2" color="textSecondary" component="p">
                  Lizards are a widespread group of squamate reptiles, with over
                  6,000 species, ranging across all continents except Antarctica
                </Typography> */}
                </CardContent>
              </CardActionArea>
              <CardActions>
                <Button size="small" color="primary">
                  {isPresent(
                    image.id,
                    image.secret,
                    image.farm,
                    image.server,
                    image.title
                  )}
                </Button>
                <Button size="small" color="primary">
                  Learn More
                </Button>
              </CardActions>
            </Card>
              </Grid>
            </Grid>

          </div>
          // <div className="center">
          //   <div className="col s6 m4">
          //     <div className="card">
          //       <div className="card-image">
          //         <img height="300" src={srcPath}></img>
          //       </div>
          //       <div className="card-action center">

          //       </div>
          //     </div>
          //   </div>
          // </div>
        );
      })}
    </div>
  );
}

export default Display;
