import React, { useState, useEffect } from "react";
import axios from "axios";
import Display from "./Display";

function Input(props) {
  const API_URL = "http://127.0.0.1:8000/test/search/";
  const [options, setOptions] = useState([]);
  const [images, setImages] = useState([]);
  const [view, setView] = useState(false);
  const [values, setValues] = useState({
    latitude: "",
    longitude: "",
  });

  const set = (name) => {
    return ({ target: { value } }) => {
      setValues((oldValues) => ({ ...oldValues, [name]: value }));
    };
  };
  const Search = () => {
    const url = "http://127.0.0.1:8000/test/search/";
    axios
      .get(API_URL, {
        params: values,
      })
      .then(function (response) {
        console.log(response);
        setImages(response.data.photos.photo);
        console.log(images);
      })
      .catch(function (error) {
        console.log(error);
      });
  };
  const onSubmit = async (event) => {
    event.preventDefault(); // Prevent default submission
    try {
      await Search();

    } catch (e) {
      alert(`Search failed! ${e.message}`);
    }
  };
  const populate = () => {
    axios
      .get("http://localhost:8000/test/populate/")
      .then((response) => {
        console.log(response.data);
        setOptions(response.data);
      })
      .catch((error) => {
        console.log(error);
      });
  };
  useEffect(() => {
    // Update the document title using the browser API
    populate();
  }, []);

  if (view){
    return( 
      <Display images = {images}/>
    );
  }
  return (
    <div className="container">
      <div>
        <h2 className="center">Picture Search By Geolocation</h2>
      </div>
      <form onSubmit={onSubmit} className="col s12">
        <div className="row">
          <div className="input-field col s6">
            <input
              placeholder="Latitude"
              id="latitude"
              value={values.latitude}
              onChange={set("latitude")}
            />
          </div>
          <div className="input-field col s6">
            <input
              placeholder="Longitude"
              id="longitude"
              value={values.longitude}
              onChange={set("longitude")}
            />
          </div>
        </div>
        <div className="center">
          <button className="btn waves-effect waves-light" name="action">
            Submit
            <i className="material-icons right">send</i>
          </button>
        </div>
      </form>
      <div className="center">
        <h2>or</h2>
      </div>
      <div className=" col s12">
        <select>
          <option value="">Location</option>
          <option>vdsvafa</option>
          {options.map((option) => (
            <option value={option.location_name} key={option.location_name}>
              {option.location_name}
            </option>
          ))}
        </select>
      </div>
    </div>
  );
}

export default Input;
