import React, { useState, useEffect } from "react";
import { useHistory, Link } from "react-router-dom";
import axios from "axios";
import { makeStyles } from "@material-ui/core/styles";
import InputLabel from "@material-ui/core/InputLabel";
import MenuItem from "@material-ui/core/MenuItem";
import FormControl from "@material-ui/core/FormControl";
import Select from "@material-ui/core/Select";

function Favourites() {
  const history = useHistory();
  const [favouriteItems, setFavouriteItems] = useState([]);
  const [locations, setLocations] = useState([]);
  const [text, setText] = useState("");

  const useStyles = makeStyles((theme) => ({
    formControl: {
      margin: theme.spacing(1),
      minWidth: 520,
    },
    selectEmpty: {
      marginTop: theme.spacing(2),
    },
  }));

  const classes = useStyles();

  const loadFavouries = (event) => {
    const API_URL = "http://127.0.0.1:8000/test/listfavourites/";
    axios
      .get(API_URL)
      .then((response) => {
        console.log(response);
        setFavouriteItems(response.data.results);
      })
      .catch((error) => {
        console.log(error);
      });
  };
  const handleTextChange = (event) => {
    setText(event.target.value);
};

  const onSubmitNote = (id,event) => {
    addNote(id, event.target.value)
  }
  const getNote = (id) => {
    return (
      <>
        <div class="row">
          <form class="col s12" onSubmit={onSubmitNote(id)}>
            <div class="row">
              <div class="input-field col s12">
                <textarea
                  value={text}
                  onChange={handleTextChange}
                  id="textarea1"
                  class="materialize-textarea"
                ></textarea>
                <label for="textarea1">Textarea</label>
              </div>
            </div>
          </form>
        </div>
      </>
    );
  };

  const addNote = (id, text) => {
    const API_URL = "http://127.0.0.1:8000/test/addnote/";

    axios
      .post(API_URL, {
        id: id,
        text: text,
      })
      .then(function (response) {
        console.log(response);
      })
      .catch(function (error) {
        console.log(error);
      });
  };
  useEffect(() => {
    loadFavouries();
  }, []);
  return (
    <React.Fragment>
      <div className="container center ">
        <h2>Favourites</h2>
      </div>
      <div className="row">
        {favouriteItems.map((image, index) => {
          console.log(image);
          let srcPath =
            "https://farm" +
            image.farm +
            ".staticflickr.com/" +
            image.server +
            "/" +
            image.id +
            "_" +
            image.secret +
            ".jpg";
          return (
            <div className="container center">
              <div className=" center">
                <div className="col s6 m4">
                  <div className="card">
                    <div className="card-image">
                      <img height="300" key={index} src={srcPath}></img>
                    </div>
                    <div className="card-action center">
                      <p>
                        <label>
                          <a
                            className="material-icons"
                            onClick={() => {
                              getNote(image.id);
                            }}
                          >
                            add Note
                          </a>
                        </label>
                      </p>
                    </div>
                  </div>
                </div>
              </div>
            </div>
          );
        })}
      </div>
    </React.Fragment>
  );
}

export default Favourites;

