import React, { useEffect, useState } from "react";
import axios from "axios";

function Image() {
  const [images, setImages] = useState([]);
  const [display, setDisplay] = useState([]);
  const [pageIndex,setPageIndex] = useState(0);
  let max_page = 1000;

  const showImages = () => {
    axios
      .get(
        "https://www.flickr.com/services/rest/?method=flickr.photos.search&api_key=f5e3dc5e6886a1e7799eeb85aad99cb5&text=chennai&per_page=30&page=1&format=json&nojsoncallback=1"
      )
      .then((response) => {
          console.log(response)
        setImages(response.data.photos.photo).then(() => {updateDisplay()})
        max_page = Math.ceil(response.data.photos.photo.length/10)
        console.log(max_page)

      });

  };
  const nextPage = () => {
      setPageIndex(pageIndex+1)
      updateDisplay()
      console.log(pageIndex)


  }
  const prevPage = () => {
        setPageIndex(pageIndex-1)
        updateDisplay()
        console.log(pageIndex)
  }

  const updateDisplay = () => {
      setDisplay(images.slice(10*pageIndex,(pageIndex+1)*10))
  }

  useEffect(() => {
    // Update the document title using the browser API
    showImages();
  }, []);
  console.log(images);
  // const srcPath = 'https://farm'+pic.farm+'.staticflickr.com/'+pic.server+'/'+pic.id+'_'+pic.secret+'.jpg';

  return (
    <div className= "container">
        {display.map((image) => {
           let srcPath =  'https://farm'+image.farm+'.staticflickr.com/'+image.server+'/'+image.id+'_'+image.secret+'.jpg';
           return(
            <div className="row">
            <div className="col s12 m7">
            <div className="card">
                <div className="card-image">
                <img src = {srcPath} ></img>
                </div>
            </div>
                </div>
                </div>

           );

        })}
        <div className = "row">
            {pageIndex != max_page ? <button className ="btn waves-effect waves-light" onClick = {nextPage}>Next</button>: <></>}
            {pageIndex != 0 ? <button className ="btn waves-effect waves-light"  onClick = {prevPage}>Prev</button>: <></>}

         {/* <button onClick = {prevPage}>Prev</button>
         <button className = "btn waves-effect waves-light" onClick = {nextPage}>next</button> */}
        </div>



    </div>
  );
}

export default Image;
