import React, { useEffect, useState } from "react";
import axios from "axios";
let id = 0;
let source = "";
let pageIndex = 0;
let max_page = 999;
function Display() {
  const API_URL = "http://127.0.0.1:8000/test/search/";
  const [images, setImages] = useState([]);
  const [display, setDisplay] = useState([]);

  const showImages = () => {
    const location = "chennai";
    axios
      .get(API_URL, {
        params: {
          latitude: "22.57",
          longitude: "88.36",
          page: 1,
        },
      })
      .then((response) => {
        console.log(response.data.photos.photo);
        setImages(response.data.photos.photo);
        max_page = Math.ceil(response.data.photos.photo.length / 10) - 1;
        console.log(max_page);
        setDisplay(response.data.photos.photo.slice(0, 10));
      });
  };
  const nextPage = () => {
    pageIndex += 1;
    updateDisplay();
    console.log(pageIndex);
  };
  const prevPage = () => {
    pageIndex -= 1;
    updateDisplay();
    console.log(pageIndex);
  };

  const updateDisplay = () => {
    setDisplay(images.slice(10 * pageIndex, (pageIndex + 1) * 10));
  };

  const addToFavourite = (id, source) => {
    const url = "http://127.0.0.1:8000/test/addfavourite/";
    axios
      .post(url, {
        location: id,
        image_src: source,
      })
      .then(function (response) {
        console.log(response);
      })
      .catch(function (error) {
        console.log(error);
      });
  };
  console.log(display, "display");
  // const srcPath = 'https://farm'+pic.farm+'.staticflickr.com/'+pic.server+'/'+pic.id+'_'+pic.secret+'.jpg';
  useEffect(() => {
    showImages();
  }, []);
  return (
    <div className="row container">
      {display.map((image) => {
        let srcPath =
          "https://farm" +
          image.farm +
          ".staticflickr.com/" +
          image.server +
          "/" +
          image.id +
          "_" +
          image.secret +
          ".jpg";
        console.log(srcPath);
        return (
          <div className="center">
            <div className="col s6 m4">
              <div className="card">
                <div className="card-image">
                  <img height="300" src={srcPath}></img>
                </div>
                <div class="card-action center">
                  <p>
                    <label>
                      <input onClick={() => console.log("checked")} type="checkbox" />
                      <span>Red</span>
                    </label>
                  </p>
                  <button
                    onClick={() => {
                      addToFavourite(1, srcPath);
                    }}
                  >
                    Add to favourites
                  </button>
                </div>
              </div>
            </div>
          </div>
        );
      })}
      <div>
        <div className="container center">
          {pageIndex != 0 ? (
            <button className="btn waves-effect waves-light" onClick={prevPage}>
              Prev
            </button>
          ) : (
            <></>
          )}

          {pageIndex < max_page ? (
            <button className="btn waves-effect waves-light" onClick={nextPage}>
              Next
            </button>
          ) : (
            <></>
          )}

          {/* <button onClick = {prevPage}>Prev</button>
         <button className = "btn waves-effect waves-light" onClick = {nextPage}>next</button> */}
        </div>
      </div>
    </div>
  );
}

export default Display;
